import React, { useState } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import Chat from "@material-ui/icons/Chat";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";

import styles from "assets/jss/material-kit-react/views/componentsSections/tabsStyle.js";
import PostItem from "./PostItem";

const useStyles = makeStyles(styles);

export default function SectionTabs() {
   const classes = useStyles();
   const posts = [
      {
         title: "post1",
         body: "body of post1",
      },
      {
         title: "post2",
         body: "body of post2",
      },
      {
         title: "post3",
         body: "body of post3",
      },
   ];

   return (
      <div className={classes.section}>
         <div className={classes.container}>
            <div id="nav-tabs">
               <h3>Post Section</h3>
               <GridContainer>
                  <PostItem />
                  {posts.map((post) => (
                     <PostItem key={post.id} post={post} />
                  ))}

                  <GridItem xs={12} sm={12} md={6}>
                     <h3>
                        <small>example post 2</small>
                     </h3>
                     <CustomTabs
                        headerColor="warning"
                        tabs={[
                           {
                              tabName: "Post",
                              tabIcon: Chat,
                              tabContent: (
                                 <p className={classes.textCenter}>
                                    JoJo's Bizarre Adventure (Japanese:
                                    ジョジョの奇妙な冒険, Hepburn: JoJo no Kimyō
                                    na Bōken) is a Japanese manga series written
                                    and illustrated by Hirohiko Araki. It was
                                    serialized in Weekly Shōnen Jump from 1987
                                    to 2004, and was transferred to the monthly
                                    seinen manga magazine Ultra Jump in 2005.
                                 </p>
                              ),
                           },
                        ]}
                     />
                  </GridItem>
               </GridContainer>
            </div>
         </div>
      </div>
   );
}
