import React, { useContext } from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// react components for routing our app without refresh
import { Link } from "react-router-dom";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// @material-ui/icons
// core components
import Header from "../../components/Header/Header.js";
import Footer from "../../components/Footer/Footer.js";
import GridContainer from "../../components/Grid/GridContainer.js";
import GridItem from "../../components/Grid/GridItem.js";
import Parallax from "../../components/Parallax/Parallax.js";
// sections for this page
import HomeLinks from "../../components/Header/HomeLinks.js";
import HeaderLinks from "../../components/Header/HeaderLinks.js";
import Posts from "./Posts.js";

import styles from "../../assets/jss/material-kit-react/views/components.js";
import AuthContext from "../../context/post/postContext";

const useStyles = makeStyles(styles);

export default function Home(props) {
   const classes = useStyles();
   const { ...rest } = props;
   const authContext = useContext(AuthContext);

   return (
      <div>
         <Header
            brand="JOJO"
            leftLinks={<HomeLinks />}
            rightLinks={<HeaderLinks />}
            fixed
            color="transparent"
            changeColorOnScroll={{
               height: 250,
               color: "white",
            }}
            {...rest}
         />
         <Parallax image={require("../../assets/img/754790.png")}>
            <div className={classes.container}>
               <GridContainer>
                  <GridItem>
                     <div className={classes.brand}>
                        <h1 className={classes.title}>JOJO FANTOM</h1>
                        <h3 className={classes.subtitle}>
                           <strong>News:</strong> While we have yet to hear any
                           confirmation about the sixth season of JoJo's Bizarre
                           Adventure, one of the voice actors for the Golden
                           Wind let it slip that the story of Jolyne Cujoh is
                           definitely on his mind as Ray Chase, the voice of
                           Bruno Bucciarati, made a reference to the Stone
                           Ocean! With the fifth season receiving a blu-ray set
                           this fall, and an interest in the franchise
                           continuing to hit new heights, fans are at the edge
                           of their seats to learn just when the anime produced
                           by David Productions will make it's grand return!
                        </h3>
                     </div>
                  </GridItem>
               </GridContainer>
            </div>
         </Parallax>

         <div className={classNames(classes.main, classes.mainRaised)}>
            <Posts />
         </div>
         <Footer />
      </div>
   );
}
