import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import EditIcon from "@material-ui/icons/Edit";

// core components
import Header from "components/Header/Header.js";
import Footer from "components/Footer/Footer.js";
import Button from "components/CustomButtons/Button.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import HomeLinks from "components/Header/HomeLinks.js";
import HeaderLinks from "components/Header/HeaderLinks.js";
import Parallax from "components/Parallax/Parallax.js";
import Description from "./Description";
import Favourites from "./Favourites";
import UserPosts from "./UserPosts";

import styles from "assets/jss/material-kit-react/views/profilePage.js";

const useStyles = makeStyles(styles);

export default function ProfilePage(props) {
   const classes = useStyles();
   const { ...rest } = props;
   const imageClasses = classNames(
      classes.imgRaised,
      classes.imgRoundedCircle,
      classes.imgFluid
   );

   return (
      <div>
         <Header
            color="transparent"
            brand="JOJO"
            leftLinks={<HomeLinks />}
            rightLinks={<HeaderLinks />}
            fixed
            changeColorOnScroll={{
               height: 200,
               color: "white",
            }}
            {...rest}
         />
         <Parallax
            small
            filter
            image={require("assets/img/profiles/754796.jpg")}
         />
         <div className={classNames(classes.main, classes.mainRaised)}>
            <div>
               <div className={classes.container}>
                  {/* Basic profile info and Description */}
                  <GridContainer>
                     <GridItem xs={12} sm={12} md={6}>
                        <div className={classes.profile}>
                           <div>
                              <img
                                 src={profile}
                                 alt="..."
                                 className={imageClasses}
                              />
                           </div>
                           <div className={classes.name}>
                              <h3 className={classes.title}>
                                 {props.user.name}
                              </h3>
                              <h4>
                                 <Button
                                    justIcon
                                    link
                                    className={classes.margin5}
                                 >
                                    <i className={"fab fa-twitter"} />
                                 </Button>
                                 <Button
                                    justIcon
                                    link
                                    className={classes.margin5}
                                 >
                                    <i className={"fab fa-instagram"} />
                                 </Button>
                                 <Button
                                    justIcon
                                    link
                                    className={classes.margin5}
                                 >
                                    <i className={"fab fa-facebook"} />
                                 </Button>
                              </h4>
                              <h4>
                                 <Button color="info" size="sm">
                                    {" "}
                                    <EditIcon /> Edit Profile
                                 </Button>
                              </h4>
                           </div>
                        </div>
                     </GridItem>
                     <GridItem xs={12} sm={12} md={6}>
                        <Description user={props.user} />
                     </GridItem>
                  </GridContainer>

                  {/* Favourite avatar and lines */}
                  <GridContainer justify="center">
                     <GridItem
                        xs={12}
                        sm={12}
                        md={12}
                        className={classes.navWrapper}
                     >
                        <Favourites />
                     </GridItem>
                  </GridContainer>

                  {/* User post section */}
                  <GridContainer justify="center">
                     <UserPosts user={props.user} />
                  </GridContainer>
               </div>
            </div>
         </div>
         <Footer />
      </div>
   );
}
