import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import PostAddIcon from "@material-ui/icons/PostAdd";
import VerifiedUser from "@material-ui/icons/VerifiedUser";
import InsertEmoticonIcon from "@material-ui/icons/InsertEmoticon";
// core components
import GridContainer from "../../../components/Grid/GridContainer.js";
import GridItem from "../../../components/Grid/GridItem.js";
import InfoArea from "../../../components/InfoArea/InfoArea.js";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";

import styles from "../../../assets/jss/material-kit-react/views/landingPageSections/productStyle.js";

const useStyles = makeStyles(styles);

export default function ProductSection() {
   const classes = useStyles();
   return (
      <div className={classes.section}>
         <GridContainer justify="center">
            <GridItem xs={12} sm={12} md={8}>
               <h2 className={classes.title}>README</h2>
               <h5 className={classes.description}>
                  <List>
                     <ListItem>1. 需注册进入主页</ListItem>
                     <ListItem>
                        2.
                        本版本的服务器后端只支持用户信息里的名字，邮箱，年龄，位置信息，职业，和管理post内容
                     </ListItem>
                     <ListItem>
                        3. 本论坛前端构造是React + Context, 后端是MongoDB；
                     </ListItem>
                  </List>
               </h5>
            </GridItem>
         </GridContainer>

         <GridContainer justify="center">
            <GridItem xs={12} sm={12} md={8}>
               <h2 className={classes.title}>Let{"'"}s Talk About Jojo</h2>
               <h5 className={classes.description}>
                  承太郎，快用你无敌的白金之星想想办法:)
               </h5>
            </GridItem>
         </GridContainer>
         <div>
            <GridContainer>
               <GridItem xs={12} sm={12} md={4}>
                  <InfoArea
                     title="Post Whatever"
                     description="anything but illegal stuff..."
                     icon={PostAddIcon}
                     iconColor="info"
                     vertical
                  />
               </GridItem>
               <GridItem xs={12} sm={12} md={4}>
                  <InfoArea
                     title="Users Protection"
                     description="we protect your personal info at the best"
                     icon={VerifiedUser}
                     iconColor="success"
                     vertical
                  />
               </GridItem>
               <GridItem xs={12} sm={12} md={4}>
                  <InfoArea
                     title="Feedback"
                     description="Send me suggestion if you have any. I hope you're happy with the forum"
                     icon={InsertEmoticonIcon}
                     iconColor="warning"
                     vertical
                  />
               </GridItem>
            </GridContainer>
         </div>
      </div>
   );
}
