import React, { useState } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";

import styles from "assets/jss/material-kit-react/views/landingPageSections/workStyle.js";
import * as emailjs from "emailjs-com";

const useStyles = makeStyles(styles);

export default function WorkSection() {
   const classes = useStyles();
   const [form, setForm] = useState({
      name: "",
      email: "",
      message: "",
   });

   const { name, email, message } = form;

   const onChange = (e) =>
      setForm({ ...form, [e.target.name]: e.target.value });

   const onSubmit = (e) => {
      e.preventDefault();
      if (email === "" || name === "" || message === "") {
         console.log("Fill in the form");
      }
      var template_params = {
         from_email: email,
         reply_to: "delseylyq@gmail.com",
         from_name: name,
         to_name: "Yuqi Liu",
         message_html: message,
      };

      var service_id = "gmail";
      var template_id = "template_fNBYV3pr";
      emailjs.send(service_id,"user_KKRp0KXxzErKuupPSWJiP" ,template_id, template_params);
   };
   return (
      <div className={classes.section}>
         <GridContainer justify="center">
            <GridItem cs={12} sm={12} md={8}>
               <h2 className={classes.title}>Contact Me</h2>
               <h4 className={classes.description}>
                  Any query about this forum
               </h4>
               <form onSubmit={onSubmit} className="contact_from_class">
                  <GridContainer>
                     <GridItem xs={12} sm={12} md={6}>
                        <CustomInput
                           labelText="Your Name"
                           id="name"
                           formControlProps={{
                              name: "name",
                              onChange: onChange,
                              fullWidth: true,
                           }}
                           inputProps={{
                              name: "name",
                              onChange: onChange,
                           }}
                        />
                     </GridItem>
                     <GridItem xs={12} sm={12} md={6}>
                        <CustomInput
                           labelText="Your Email"
                           id="email"
                           formControlProps={{
                              fullWidth: true,
                           }}
                           inputProps={{
                              name: "email",
                              onChange: onChange,
                           }}
                        />
                     </GridItem>
                     <CustomInput
                        labelText="Your Message"
                        id="message"
                        formControlProps={{
                           fullWidth: true,
                           className: classes.textArea,
                        }}
                        inputProps={{
                           name: "message",
                           onChange: onChange,
                           multiline: true,
                           rows: 5,
                        }}
                     />
                     <GridItem xs={12} sm={12} md={4}>
                        <Button color="primary" >
                           Send Message
                        </Button>
                     </GridItem>
                  </GridContainer>
               </form>
            </GridItem>
         </GridContainer>
      </div>
   );
}
