import React, { Fragment } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import Chat from "@material-ui/icons/Chat";
// core components
import GridItem from "components/Grid/GridItem.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";

import styles from "assets/jss/material-kit-react/views/componentsSections/tabsStyle.js";

const useStyles = makeStyles(styles);
const PostItem = (post, ) => {
   const classes = useStyles();
   return (
      <Fragment>
         <GridItem xs={12} sm={12} md={6}>
            <h3>
               <small>example post 2</small>
            </h3>
            <CustomTabs
               headerColor="info"
               tabs={[
                  {
                     tabName: "Post",
                     tabIcon: Chat,
                     tabContent: (
                        <p className={classes.textCenter}>
                           JoJo's Bizarre Adventure (Japanese:
                           ジョジョの奇妙な冒険, Hepburn: JoJo no Kimyō na
                           Bōken) is a Japanese manga series written and
                           illustrated by Hirohiko Araki. It was serialized in
                           Weekly Shōnen Jump from 1987 to 2004, and was
                           transferred to the monthly seinen manga magazine
                           Ultra Jump in 2005.
                        </p>
                     ),
                  },
               ]}
            />
         </GridItem>
      </Fragment>
   );
};

export default PostItem;
