import React, {useContext, useEffect} from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// core components
import SectionCarousel from "../Components/Carousel";
import GridContainer from "components/Grid/GridContainer.js";
import styles from "assets/jss/material-kit-react/views/componentsSections/tabsStyle.js";
import PostItem from "./PostItem";
import PostMake from "./PostMake";

// context
import PostContext from "../../context/post/postContext";

const useStyles = makeStyles(styles);

const Posts = () => {
   const classes = useStyles();
   const postContext = useContext(PostContext);
   const { getPost } = postContext;

   const posts = [
      {
         title: "post1",
         body:
            "JoJo's Bizarre Adventure (Japanese: ジョジョの奇妙な冒険, Hepburn: JoJo no Kimyō na Bōken) is a Japanese manga series written and illustrated by Hirohiko Araki. It was serialized in Weekly Shōnen Jump from 1987 to 2004, and was transferred to the monthly seinen manga magazine Ultra Jump in 2005.",
      },
      {
         title: "post2",
         body:
            "JoJo's Bizarre Adventure (Japanese: ジョジョの奇妙な冒険, Hepburn: JoJo no Kimyō na Bōken) is a Japanese manga series written and illustrated by Hirohiko Araki. It was serialized in Weekly Shōnen Jump from 1987 to 2004, and was transferred to the monthly seinen manga magazine Ultra Jump in 2005.",
      },
      {
         title: "post3",
         body:
            "JoJo's Bizarre Adventure (Japanese: ジョジョの奇妙な冒険, Hepburn: JoJo no Kimyō na Bōken) is a Japanese manga series written and illustrated by Hirohiko Araki. It was serialized in Weekly Shōnen Jump from 1987 to 2004, and was transferred to the monthly seinen manga magazine Ultra Jump in 2005.",
      },
   ];

   return (
      <div className={classes.section}>
         <div className={classes.container}>
            <div id="nav-tabs">
               <h2>Post Section</h2>
               <GridContainer>
                  {posts.map((post) => (
                     <PostItem key={post.id} post={post} color="info" />
                  ))}
               </GridContainer>
            </div>

            <div>
               <SectionCarousel />
            </div>

            <div>
               <PostMake />
            </div>
         </div>
      </div>
   );
}

export default Posts;
