import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons

// core components
import Header from "components/Header/Header.js";
import Footer from "components/Footer/Footer.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Button from "components/CustomButtons/Button.js";
import HeaderLinks from "components/Header/HeaderLinks.js";
import Parallax from "components/Parallax/Parallax.js";

import styles from "assets/jss/material-kit-react/views/landingPage.js";

// Sections for this page
import ProductSection from "./Sections/ProductSection.js";
import TeamSection from "./Sections/TeamSection.js";
import WorkSection from "./Sections/WorkSection.js";

const dashboardRoutes = [];

const useStyles = makeStyles(styles);

export default function LandingPage(props) {
   const classes = useStyles();
   const { ...rest } = props;
   return (
      <div>
         <Header
            color="transparent"
            routes={dashboardRoutes}
            brand="JOJO"
            rightLinks={<HeaderLinks />}
            fixed
            changeColorOnScroll={{
               height: 400,
               color: "white",
            }}
            {...rest}
         />
         <Parallax image={require("assets/img/754790.png")}>
            <div className={classes.container}>
               <GridContainer>
                  <GridItem xs={12} sm={12} md={6}>
                     <h1 className={classes.title}>Jojo's Bizarre Adventure</h1>
                     <h4>
                        JoJo's Bizarre
                        Adventure (Japanese: ジョジョの奇妙な冒険, Hepburn: JoJo
                        no Kimyō na Bōken) is a Japanese manga series written
                        and illustrated by Hirohiko Araki. It was serialized in
                        Weekly Shōnen Jump from 1987 to 2004, and was
                        transferred to the monthly seinen manga magazine Ultra
                        Jump in 2005. The series is divided into eight story
                        arcs, each following the adventures of a new protagonist
                        bearing the "JoJo" nickname; the eighth, JoJolion, began
                        its ongoing serialization in May 2011. JoJo's Bizarre
                        Adventure is Shueisha's second largest manga by volume
                        count, with its chapters collected in 127 tankōbon
                        volumes as of April 2020.
                     </h4>
                     <br />
                     <Button
                        color="primary"
                        size="lg"
                        href="https://youtu.be/1ajlgvriyLY"
                        target="_blank"
                        rel="noopener noreferrer"
                     >
                        <i className="fas fa-play" />
                        Watch video
                     </Button>
                  </GridItem>
               </GridContainer>
            </div>
         </Parallax>
         <div className={classNames(classes.main, classes.mainRaised)}>
            <div className={classes.container}>
               {/* <ProductSection />
               <TeamSection />
               <WorkSection /> */}
            </div>
         </div>
         <Footer />
      </div>
   );
}
