import {
    GET_POST,
    GET_USER_POST,
    ADD_POST,
    DELETE_POST,
    POST_ERROR,
    SET_CURRENT,
 } from "../type";

 export default (state, action) => {
     switch(action.type){
         case GET_POST:
         return{
             ...state,
             posts: action.payload,
             loading: false,
         }

         case GET_USER_POST:
         return{
             ...state,
         }

         case ADD_POST:
         return{
             ...state,
             loading:false,
             posts: [action.payload, ...state.posts]
         }
         case DELETE_POST:
         return{
             ...state,
             loading: false,
             posts: state.posts.filter(
                (post) => post._id !== action.payload
             ),
         }
         case POST_ERROR:
         return{
             ...state,
             error: action.payload
         }

         default:
         return state;
     }
 }
