import React, { Fragment } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// @material-ui/icons
import Chat from "@material-ui/icons/Chat";
import DeleteIcon from '@material-ui/icons/Delete';

// core components
import GridItem from "components/Grid/GridItem.js";
import CustomTabs from "components/CustomTabs/CustomTabs.js";
import Button from "components/CustomButtons/Button.js"

import styles from "assets/jss/material-kit-react/views/componentsSections/tabsStyle.js";

const useStyles = makeStyles(styles);
const UserPostItem = ({ post }) => {
   const classes = useStyles();

   const headerColor = "info";
   const { title, body } = post;

   return (
      <Fragment>
         <GridItem xs={12} sm={12} md={6}>
            <CustomTabs
               headerColor={headerColor}
               tabs={[
                  {
                     tabName: "Post",
                     tabIcon: Chat,
                     tabContent: (
                        <Fragment>
                            {/* <Button color="info" size="sm" justIcon="true" ><DeleteIcon/></Button> */}
                           <h3 color="primary">{title}</h3>
                           <p className={classes.textCenter}>{body}</p>
                        </Fragment>
                     ),
                  },
                  {
                  tabName: "Delete",
                   tabButton:<Button color="info" size="sm" justIcon="true" ><DeleteIcon/></Button>,

                  }
               ]}
            />

         </GridItem>
      </Fragment>
   );
};

export default UserPostItem;
