import { SET_ALERT, REMOVE_ALERT, SET_SUCCESS, REMOVE_SUCCESS } from "../type";

export default (state, action) => {
   switch (action.type) {
      case SET_ALERT:
         return {
            ...state,
            alerts: [...state.alerts, action.payload]
         }

      case REMOVE_ALERT:
         return state.filter((alert) => alert.id !== action.payload);

      case SET_SUCCESS:
         return {
            ...state,
            success: [...state.success, action.payload]
         }

      case REMOVE_SUCCESS:
         return state.filter((alert) => alert.id !== action.payload);

      default:
         return state;
   }
};
